can_activate:
if self.has_status("MEW_ACTIVATED_EFFECT"):
  return self.interp(self.mimic.can_activate)
else:
  return can_target_field() and owner.mana >= 1

if_summon_cost:
will_activate = owner.graveyard.contains(lambda card: card.cost == 2) and owner.can_pay(1) and prompt_user_activate(self.name);
if will_activate:
  owner.pay(1)
return will_activate

on_activate:
if not self.has_status("ACTIVATED_EFFECT"):
  for card in owner.board:
    if card != self:
      io.remove_field(card, player="turn")
  for card in other.board:
    io.remove_field(card, player="other")
  for card in owner.board:
    if card != self:
      io.send_graveyard(card, player="turn", update_board=False)
  for card in other.board:
    io.send_graveyard(card, player="other", update_board=False)
  self.status.append(("ACTIVATED_EFFECT", -1))

end_main_phase_2:
for card in owner.board:
    if "tyranitar" not in card.name.lower():
      io.send_graveyard(card, player="turn", update_board=False)
  for card in other.board:
    io.send_graveyard(card, player="other", update_board=False)

on_end_phase:
for _ in range(8):
  field = [card for card in other.field if card.health > 0]
  if field:
    random.choice(field).take_damage(self, 100)

can_summon:
for card in owner.board:
  if "magikarp" in card.name.lower():
    return True
return False
