arceus.on_activate:
if not self.has_status("ACTIVATED_EFFECT"):
  for card in owner.board:
    if card != self:
      io.remove_field(card, player="turn")
  for card in other.board:
    io.remove_field(card, player="other")
  for card in owner.board:
    if card != self:
      io.send_graveyard(card, player="turn", update_board=False)
  for card in other.board:
    io.send_graveyard(card, player="other", update_board=False)
  self.status.append(("ACTIVATED_EFFECT", -1))

